package practice;

public class firstCode {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Arithmetic Operations
		int a=10;
		int b=20;
		System.out.println("a+b=" + (a+b));
		System.out.println("a-b=" + (a-b));
		for(int i=0; i<5; i++)//for loop
			System.out.println(i);
		int i=0;
		do {
			System.out.println(i);
			i++;
		}while(i<=10);

	}

}
